import React from 'react'
import { MenuItems } from "./MenuItems"
import "./Navbar.css"
import { Link, useMatch, useResolvedPath } from "react-router-dom"

// For routing:
// function CustomLink({ href, children, ...props }) {
//   const path = window.location.pathname

//   return(
//     <li className={path === href ? "active" : ""}>
//       <a href={href} {...props}>{children}</a>
//     </li>
//   )
// }


// Navbar
class Navbar extends React.Component {
  state = { clicked: false }

  handleClick = () => {
    this.setState({ clicked: !this.state.clicked })
  }

  render() {
    return(
      <nav className="NavbarItems">
        <h1 className="navbar-logo">Polly Liu <i className="fas fa-star"></i></h1>
        <div className="menu-icon" onClick={this.handleClick}>
          <i className={this.state.clicked ? "fas fa-times" : "fas fa-bars"}></i> {/* This uses fontawesome for icons */}
        </div>
        <ul className={this.state.clicked ? "nav-menu active" : "nav-menu"}>
          {/* Creating all menu items */}
          {MenuItems.map((item, index) => {
            const resolvedPath = useResolvedPath(item.url)
            const isActive = useMatch({ path: resolvedPath.pathname, end: true })
            return (
              <li key={index}>
                <Link className={item.cName} to={item.url}
                // Only open a new tab if you want resume or to contact me
                target={(item.title==="Resume" || item.title ==="Contact") ? "_blank" : null}>
                  {item.title}
                </Link>
              </li>
            )
          })}
        </ul>
      </nav>
    )
  }
}

export default Navbar
